import React, { useState, useEffect } from 'react';
import './estilos.css';
// Función auxiliar para obtener los resultados reales desde algún lugar (ahora simularemos los datos)
async function obtenerResultadosReales() {
  return new Promise((resolve) => {
    // Simulación de los datos (reemplaza esto con tus resultados reales)
    const resultadosSimulados = {
      candidatos: ['Candidato A', 'Candidato B', 'Candidato C'],
      votos: [120, 150, 90],
    };
    setTimeout(() => {
      resolve(resultadosSimulados);
    }, 1000); // Simulamos una pequeña espera para emular una solicitud asincrónica
  });
}

const Resultados = () => {
  const [resultados, setResultados] = useState(null);

  useEffect(() => {
    obtenerResultadosReales()
      .then((data) => setResultados(data))
      .catch((error) => setResultados(null));
  }, []);

  const generarGraficoResultados = () => {
    if (!resultados) {
      return null;
    }

    const maxVotos = Math.max(...resultados.votos);

    return (
      <div className="grafico-container">
        {resultados.candidatos.map((candidato, index) => (
          <div key={candidato} className="barra">
            <div className="nombre-candidato">{candidato}</div>
            <div className="barra-votos">
              <div
                className="barra-llenada"
                style={{ width: `${(resultados.votos[index] / maxVotos) * 100}%` }}
              />
            </div>
            <div className="votos">{resultados.votos[index]}</div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div>
      <h1>Resultados de la elección</h1>
      {resultados ? (
        generarGraficoResultados()
      ) : (
        <p>Obteniendo resultados...</p>
      )}
    </div>
  );
};

export default Resultados;
