import React from 'react';

// Supongamos que ya existe una variable resultadosElecciones con los resultados reales
const resultadosElecciones = [
  { candidato: 'Candidato A', votos: 150 },
  { candidato: 'Candidato B', votos: 200 },
  { candidato: 'Candidato C', votos: 100 },
];

// Componente para mostrar un gr치fico de barras simple para los resultados
function GraficoBarras({ resultados }) {
  const maxVotos = Math.max(...resultados.map((resultado) => resultado.votos));

  return (
    <div>
      <h3>Resultados en gr치fico de barras</h3>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        {resultados.map((resultado, index) => (
          <div
            key={index}
            style={{
              width: '100px',
              marginRight: '20px',
              textAlign: 'center',
              backgroundColor: '#f0f0f0',
              padding: '5px',
            }}
          >
            <div style={{ height: `${(resultado.votos / maxVotos) * 100}%`, backgroundColor: '#007bff' }}>
              <span style={{ color: 'white' }}>{resultado.votos}</span>
            </div>
            <span>{resultado.candidato}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

// Componente principal que representa el flujo descrito
function FlujoResultadosElecciones() {
  return (
    <div>
      <h2>Resultados de la Elecci칩n</h2>
      <ul>
        {resultadosElecciones.map((resultado, index) => (
          <li key={index}>
            {resultado.candidato}: {resultado.votos} votos
          </li>
        ))}
      </ul>
      <GraficoBarras resultados={resultadosElecciones} />
    </div>
  );
}

export default FlujoResultadosElecciones;
