import React, { useState, useEffect } from 'react';
import Chart from 'chart.js/auto';

// Función auxiliar para obtener los resultados reales desde algún lugar (igual que antes)
// ...

const Resultados = () => {
  const [resultados, setResultados] = useState(null);

  useEffect(() => {
    obtenerResultadosReales()
      .then((data) => setResultados(data))
      .catch((error) => setResultados(null));
  }, []);

  useEffect(() => {
    if (resultados) {
      // Aquí, llamamos a la función para generar el gráfico
      generarGraficoResultados();
    }
  }, [resultados]);

  const generarGraficoResultados = () => {
    const canvasRef = document.getElementById('grafico-resultados');

    // Configuración del gráfico
    const data = {
      labels: resultados.candidatos,
      datasets: [
        {
          label: 'Votos recibidos',
          data: resultados.votos,
          backgroundColor: ['rgba(75, 192, 192, 0.6)', 'rgba(255, 99, 132, 0.6)', 'rgba(54, 162, 235, 0.6)'],
          borderColor: ['rgba(75, 192, 192, 1)', 'rgba(255, 99, 132, 1)', 'rgba(54, 162, 235, 1)'],
          borderWidth: 1,
        },
      ],
    };

    const options = {
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        y: {
          beginAtZero: true,
        },
      },
    };

    // Crear el gráfico con Chart.js
    new Chart(canvasRef, {
      type: 'bar',
      data: data,
      options: options,
    });
  };

  return (
    <div>
      <h1>Resultados de la elección</h1>
      {resultados ? (
        <canvas id="grafico-resultados" width="400" height="200" />
      ) : (
        <p>Obteniendo resultados...</p>
      )}
    </div>
  );
};

export default Resultados;
